<p>MySQL query:</p>
<p>CREATE FUNCTION `wp_erpc_do_lock`(`name` VARCHAR(100), `timeout` INT, `owner` INT) RETURNS int(11)
BEGIN

   SET @lck_name = 'erpc_lck';
   SET @lck_result = 0;
   SET @result = 0;

   SELECT GET_LOCK(@lck_name, 10) INTO @lck_result;

   IF @lck_result = 1 THEN

       DELETE FROM wp_erpc_locks WHERE TIMESTAMPDIFF(SECOND, lock_time, CURRENT_TIMESTAMP) >= lock_timeout;

       SELECT 0 INTO @lck_result FROM wp_erpc_locks WHERE lock_name = name;
       IF @lck_result = 1 THEN
             INSERT INTO `wp_erpc_locks`(`lock_name`, `lock_timeout`, `lock_owner`) VALUES (name, timeout, owner);
             SET @result = 1;
       END IF;


       SELECT RELEASE_LOCK(@lck_name) INTO @lck_result; 

   END IF;

   RETURN @result;

END</p>
